package com.basics;

public class ComparisonOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Integer a=10;
		Integer b=20;
		Integer c=10;
		
		if(a==b)
			System.out.println("a==b");
		else if(a!=b && a==c)
			System.out.println("a!=b && a==c");
		else if(a==c)
			System.out.println("a==c");
		else 
			System.out.println("else all");
		
		// == <= relational operator
		//  && || logical operators

	}

}
