package com.basics;

public class JavaString {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String value="My String";
		System.out.println("value of string = "+value);
		
		String valueConcatinate="My"+" String";
		System.out.println("value concatinate ="+valueConcatinate);
		
		System.out.println(valueConcatinate.indexOf('S'));
		System.out.println("uppercase string = "+valueConcatinate.toUpperCase());
		
		// look there are many functions for string in java

	}

}
